import React, { useState } from 'react';
import Board from './components/Board';
import Preflop from './components/Preflop';
import Flop from './components/Flop';
import Turn from './components/Turn';
import River from './components/River';
import { PokerContext } from './helpers/Contexts';
import './App.css';

function App() {
  // 紀錄目前應該渲染的頁面
  const [gameState, setGameState] = useState("preflop")
  // 全域變數來紀錄目前分數
  const [pot, setPot] = useState(0)

  return (
    <div className="App">
      {/* 軟體標題 */}
      <h1 className='title'>Poker Note</h1>

      <div className='container'>
        {/* 第幾條街的 title */}
        <div className='street-title'>
          {gameState === "preflop" && "PREFLOP"}
          {gameState === "flop" && "FLOP"}
          {gameState === "turn" && "TURN"}
          {gameState === "river" && "RIVER"}
        </div>

        {/* 第幾條街的 title */}
        <div className='street-title'>
          {gameState === "preflop" && "PREFLOP"}
          {gameState === "flop" && "FLOP"}
          {gameState === "turn" && "TURN"}
          {gameState === "river" && "RIVER"}
        </div>

        {/* 渲染第幾條街 */}
        <div className='row-content'>
          {/* 用 Context API 來串接數據，讓每個頁面都有權限使用 */}
          <PokerContext.Provider value={{ gameState, setGameState }}>
            {/* 用 gameState 來判斷要渲染哪一個頁面 */}
            {gameState === "preflop" && <Preflop />}
            {gameState === "flop" && <Flop />}
            {gameState === "turn" && <Turn />}
            {gameState === "river" && <River />}
          </PokerContext.Provider>

        </div>
      </div>
    </div>
  );
}

export default App;
