import './App.css';
import React, {useState} from 'react';
import Menu from './components/Menu';

function App() {
  const [gameState, setGameState] = useState("menu")

  return (
    <div className="App">
      <h1>Quiz App</h1>

      {/* 用 gameState 來判斷要渲染哪一個頁面 */}

      {gameState === "menu" && <Menu />}
    </div>
  );
}

export default App;
