import React, { useState } from 'react';
import Board from './components/Board';
import Preflop from './components/Preflop';
import Flop from './components/Flop';
import Turn from './components/Turn';
import River from './components/River';
import { PokerContext } from './helpers/Contexts';
import './App.css';

function App() {
  // 紀錄目前應該渲染的頁面
  const [gameState, setGameState] = useState("preflop")
  // // 全域變數來紀錄目前分數
  // const [pot, setPot] = useState(0)

  return (
    <div className="App">
      {/* App Title */}
      <h1 className='title'>Poker Note</h1>

      <div className='container'>
        {/* Street Title */}
        <div className='row'>
          {gameState === "preflop" && "PREFLOP"}
          {gameState === "flop" && "FLOP"}
          {gameState === "turn" && "TURN"}
          {gameState === "river" && "RIVER"}
        </div>

        {/* Board */}
        <div className='row'>
          <Board />
        </div>

        {/* Action */}
        <div className='action'>
          <div className='player1'>
            Player1
          </div>
          <div className='player2'>
            player2
          </div>

        </div>
      </div>
    </div>
  );
}

export default App;
