import React, { useContext, useStatem, useRef } from 'react'
import { Streets } from '../helpers/StreetName'
import { PokerContext } from '../helpers/Contexts'
import '../App.css'


function Action() {
    const { currStreet, setCurrStreet, pot, setPot } = useContext(PokerContext);
    const raiseRef = useRef(null);
    const submitHandler = () => {
        setPot(parseFloat(pot) + parseFloat(raiseRef.current.value))
        { Streets[currStreet].action.push("Player1 raise $" + raiseRef.current.value) }
    };

    return (
        <div>
            <input type="text" ref={raiseRef} placeholder="$" />
            <button onClick={submitHandler}>raise</button>
        </div>
    )
}

export default Action
