import React, { useContext } from 'react'
import { Streets } from '../helpers/StreetInfo'
import { PokerContext } from '../helpers/Contexts'
import '../App.css'

function Flop() {
    const { currStreet, setCurrStreet, pot, setPot } = useContext(PokerContext)
    let data = Streets[currStreet]
    const listItems = data.action.map((number) =>
        <li>{number}</li>
    );

    return (
        < div >
            <span>Pot: {data.pot}</span>
            <ul>{listItems}</ul>
        </div >
    )
}

export default Flop
