import React, { useState } from 'react';
import Preflop from './components/Preflop';
import Flop from './components/Flop';
import Turn from './components/Turn';
import River from './components/River';
import { PokerContext } from './helpers/Contexts';
import './App.css';

function App() {
  const [gameState, setGameState] = useState("preflop")

  return (
    <div className="App">
      <h1>Poker Note App</h1>
      {/* 用 Context API 來串接數據，讓每個頁面都有權限使用 */}
      <PokerContext.Provider value={{ gameState, setGameState }}>
        {/* 用 gameState 來判斷要渲染哪一個頁面 */}
        {gameState === "preflop" && <Preflop />}
        {gameState === "flop" && <Flop />}
        {gameState === "turn" && <Turn />}
        {gameState === "river" && <River />}
      </PokerContext.Provider>
    </div>
  );
}

export default App;
